import React, { useRef, useCallback, useEffect } from 'react';
import Webcam from 'react-webcam';

const UploadSection = () => {
  const webcamRef = useRef(null);
  const [imageSrc, setImageSrc] = React.useState(null);

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImageSrc(imageSrc);
  }, [webcamRef]);

  useEffect(() => {
    // Optional: Log available devices to find the NVIDIA Broadcast camera
    navigator.mediaDevices.enumerateDevices().then(devices => {
      devices.forEach(device => {
        console.log(device.label, device.deviceId);
      });
    });
  }, []);

  return (
    <div className="bg-white p-8 rounded-lg shadow-lg w-full h-[75vh] flex flex-col items-center justify-center border-2 border-dashed border-gray-300 relative">
      <div className="w-full">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
          height="100%"
          videoConstraints={{ 
            facingMode: "user", 
            deviceId: "f0485ccf24c359cc51dcea65988f0bf53d93d30fc9a7be696c043f78a54fb99c" // Replace with the actual device ID
          }}
          style={{ objectFit: 'cover' }}
        />
      </div>

      
      
    </div>
  );
};

export default UploadSection;
