import React, { useRef, useCallback } from 'react';
import Webcam from 'react-webcam';

const UploadSection = () => {
  const webcamRef = useRef(null);
  const [imageSrc, setImageSrc] = React.useState(null);

  const capture = useCallback(() => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImageSrc(imageSrc);
  }, [webcamRef]);

  return (
    <div className="bg-white p-8 rounded-lg shadow-lg w-full h-[75vh] flex flex-col items-center justify-center border-2 border-dashed border-gray-300 relative">
      <div className="absolute inset-0">
        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width="100%"
          height="20%"
          videoConstraints={{ facingMode: "user" }}
          style={{ objectFit: 'cover' }} // Ensure the video covers the entire area
        />
      </div>

     
    </div>
  );
};

export default UploadSection;
